import * as React from 'react';
import PropTypes from 'prop-types';
import Button from '@mui/material/Button';
import { styled } from '@mui/material/styles';
import Dialog from '@mui/material/Dialog';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContent from '@mui/material/DialogContent';
import DialogActions from '@mui/material/DialogActions';
import Row from 'react-bootstrap/Row'
import Container from 'react-bootstrap/Container';
import Col from 'react-bootstrap/Col'
import IconButton from '@mui/material/IconButton';
import CloseIcon from '@mui/icons-material/Close';
import RefreshIcon from '@mui/icons-material/Refresh';
import DeleteIcon from '@mui/icons-material/Delete';
import TextField from '@mui/material/TextField';
import { useSelector } from 'react-redux'
import { incrementCart } from '../../reducers/cartReducer'
import { getAllProducts, getAllCategories, getProductsByCat } from '../../reducers/productReducer'


const BootstrapDialog = styled(Dialog)(({ theme }) => ({
  '& .MuiDialogTitle-root': {
    background: "#00",
  },
  '& .MuiDialogContent-root': {
    padding: theme.spacing(2),
  },
  '& .MuiDialogActions-root': {
    padding: theme.spacing(1),
  },
}));

const BootstrapDialogTitle = (props) => {
  const { children, onClose, ...other } = props;

  return (
    <DialogTitle sx={{ m: 0, p: 2 }} {...other}>
      {children}
      {onClose ? (
        <IconButton
          aria-label="close"
          onClick={onClose}
          sx={{
            position: 'absolute',
            right: 8,
            top: 8,
            color: (theme) => theme.palette.grey[500],
          }}
        >
          <CloseIcon />
        </IconButton>
      ) : null}
    </DialogTitle>
  );
};

BootstrapDialogTitle.propTypes = {
  children: PropTypes.node,
  onClose: PropTypes.func.isRequired,
};

export default function CustomizedDialogs({ open, handleClose }) {


  const { cart_items } = useSelector(
    (state) => state.cart
  )


   const dispatch = useDispatch()

   React.useEffect(() => {

    }, [cart_items]);

  return (
    <div>
      <BootstrapDialog
        maxWidth='md'
        scroll='paper'
        onClose={handleClose}
        aria-labelledby="customized-dialog-title"
        open={open}
      >
        <BootstrapDialogTitle id="customized-dialog-title" onClose={handleClose}>
           Cart
        </BootstrapDialogTitle>
        <DialogContent dividers>
          <Container  style={{ overflow: "scrollY", maxHeight: "300px"}}>
           { cart_items?.length == 0 ? <h3 className="d-flex" > You have no items </h3> : 
             cart_items.map((item) => ( 
            <>
            <Row md={12} className="py-2 text-center m-auto">
            <Col md={3}>
            <img style={{ maxWidth: "100px", maxHeight: "100px" }} className='position-relative' src={`sport_images/${item.image}`} alt="logo"/>
            </Col>
            <Col md={3} className="m-auto">
                {item.desc}
            </Col>
            <Col md={2} className="m-auto">
                {item.price}$
            </Col>
            <Col md={2} className="m-auto">
            <TextField
          style={{ width: '50%'}}
          defaultValue="0"  
          InputProps={{ inputProps: { min: 0} }}
          type="number"
          InputLabelProps={{
            shrink: true,
          }}
          variant="standard"
        />             
           </Col>
             <Col md={2} className="m-auto">
             <IconButton> <RefreshIcon /> </IconButton> <IconButton>  <DeleteIcon style={{color: "red",}} /> </IconButton> 
             </Col>
          </Row>
          <hr/>
          </> ))
          }
         </Container>
        </DialogContent>
        <DialogActions>
          <Button autoFocus onClick={handleClose}>
            Save changes
          </Button>
        </DialogActions>
      </BootstrapDialog>
    </div>
  );
}
