const initialState = {
  cart_items: [],
  item: {},
};

export const cartSlice = createSlice({
  name: 'cart',
  initialState,
  reducers: {
    resetCart: () => initialState,
    incrementCart: (state, action) => {
      return {
        cart_items: [ ...state.cart_items,
        action.payload],
        item: action.payload
      };
    },
    deleteFromCart: (state, action) => {
      var new_items = state.cart_items.filter((c) => c.id !== action.payload.id) 
      return { ...state, cart_items: new_items };
    }  
  },
})

export const { resetCart, incrementCart, deleteFromCart  } = cartSlice.actions
export default cartSlice.reducer
