import { INCREMENT_CART, DELETE_FROM_CART } from '../actions/types';

const initialState = {
  cart_items: [],
  item: {},
};

export default function(state = initialState, action) {
  switch (action.type) {
    case INCREMENT_CART:
      console.log(state.cart_items)
      return {
        cart_items: [ ...state.cart_items,
        action.payload],
        item: action.payload
      };
    case DELETE_FROM_CART:
      var new_items = state.cart_items.filter((c) => c.id !== action.payload.id) 
      return { ...state, cart_items: new_items };
    default:
      return state;
  }
}
