import { INCREMENT_CART, DELETE_FROM_CART } from './types';

export const cartIncrement = (item) => dispatch => {
      dispatch({
        type: INCREMENT_CART,
        payload: item
      });
};

export const deleteFromCart = (id) => dispatch => {
      dispatch({
        type: DELETE_FROM_CART,
        payload: id
      });
};
