import React from 'react'
import Badge from '@mui/material/Badge';
import ShoppingCartIcon from "@mui/icons-material/ShoppingCart";
import ShoppingCartOutlinedIcon from '@mui/icons-material/ShoppingCartOutlined';
import MenuIcon from '@mui/icons-material/Menu';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';
import { makeStyles, withStyles } from "@material-ui/core/styles";
import { css } from '@emotion/react';
import {
  ThemeProvider,
  createTheme,
  experimental_sx as sx,
} from '@mui/material/styles';
import { useState } from 'react';
//import { AiOutlineShoppingCart  } from 'react-icons/ai';

export default function Nav() {

  const StyledMenu = withStyles({
    paper: {
      border: "50px solid #d3d4d5",
      backgroundColor: "blue"
    }
  })(props => (
    <Menu
      elevation={0}
      getContentAnchorEl={null}
      anchorOrigin={{
        vertical: "bottom"
      }}
      transformOrigin={{
        vertical: "top"
      }}
      {...props}
    />
  ));

  const useStyles = makeStyles(() => ({
    menu: {
      "& .MuiPaper-root": {
        border: "50px solid #d3d4d5",
        backgroundColor: "blue"
      }
    }
  }));


  const [itemCount, setItemCount] = useState(0)
  const [anchorEl, setAnchorEl] = React.useState(null);
  const open = Boolean(anchorEl);
  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };
  const handleClose = () => {
    setAnchorEl(null);
  };

  return (
        <div className='navbar'>
        <div className='logo'>
        <img src="sport_images/logo.png" alt="logo" />
        </div>
        <div>
          <ul>
            <li className='brd d-none d-sm-none d-md-block'>HOME</li>
            <li className='brd d-none d-sm-none d-md-block'>COMPANY</li>
            <li className='brd d-none d-sm-none d-md-block'>SPORTS STARS</li>
            <li className='d-none d-sm-none d-md-block'>PRODUCTS</li>
            <li 
        id="basic-button"
        onClick={handleClick}
        aria-controls={open ? 'basic-menu' : undefined}
        aria-haspopup="true"
        aria-expanded={open ? 'true' : undefined}
         className='d-xs-block d-sm-block d-md-none'> <MenuIcon /></li>
            <li onClick={() => setItemCount(itemCount+1)}> <Badge color="primary" badgeContent={itemCount}>
             { itemCount !== 0 ? <ShoppingCartIcon /> : <ShoppingCartOutlinedIcon /> } {" "} 
        </Badge></li>
      <Menu
        id="basic-menu"
        anchorEl={anchorEl}
        open={true}
        onClose={handleClose}
        classeName={{ paper: useStyles.menu }}
      >
        <MenuItem onClick={handleClose}>HOME</MenuItem>
        <MenuItem onClick={handleClose}>COMPANY</MenuItem>
        <MenuItem onClick={handleClose}>PRODUCTS</MenuItem>
      </Menu>
          </ul>
          </div>
      </div>
  )
}
